import React, { useState, useEffect, useRef } from "react";
import "./MoveItems.css";
import { useTranslation } from "react-i18next";
import { arrayMoveImmutable } from "array-move";
import ListItems from "../ListItems/ListItems";

// "circle-s",
// "square-s",
// "oval-s",
// "rectangle-s",
// "trapezoid-s",
// "palallelogram-s",

function MoveItems() {
  const { t, i18n } = useTranslation();

  const [arrshape, setArrshape] = useState(["0", "1", "2", "3", "4", "5"]);
  const [moveLeft, setMoveLeft] = useState(false);
  const [moveRight, setMoveRight] = useState(false);

  const arrshapeRef = useRef(["0", "1", "2", "3", "4", "5"]);

  useEffect(() => {
    arrshapeRef.current.push(arrshapeRef.current.shift());
    setArrshape(arrshapeRef.current);
  }, [moveLeft]);

  useEffect(() => {
    arrshapeRef.current.push(arrshapeRef.current.shift());
    setArrshape(arrshapeRef.current);
  }, [moveRight]);
  const movePosition = (arrshapeRef) => {
    for (let i = 0; i < 3; i++) {
      let temp = arrshapeRef[i];
      arrshapeRef[i] = arrshapeRef[i + 3];
      arrshapeRef[i + 3] = temp;
    }
    console.log(arrshapeRef);
  };

  useEffect(() => {
    console.log(arrshape);
  }, []);

  return (
    <div>
      <div className="MoveItems">
        <ul className="move-items-container">
          <li className="move-items-button-container">
            <button
              onClick={setMoveLeft(!moveLeft)}
              className="move-button shape"
            >
              <div className="left-arrow"></div>
              <p className="move-shape-b">{t("Move shape")}</p>
            </button>
            <button onClick={movePosition} className="move-button position">
              <div className="up-arrow"></div>
              <div className="down-arrow"></div>
              <p className="move-position-b">{t("Move position")}</p>
            </button>
            <button
              onClick={setMoveRight(!moveRight)}
              className="move-button shape"
            >
              <div className="right-arrow"></div>
              <p className="move-shape-b">{t("Move shape")}</p>
            </button>
          </li>
          <div className="underline"></div>
        </ul>
      </div>
      <ListItems arrshape={arrshape} />
    </div>
  );
}

export default MoveItems;
